mapper.select:

        where d.dr = 0

			AND d.org_id = #{orgId}


			AND d.parent_id = #{parentId}


			AND d.dept_name like concat('%', #{deptName}, '%')


			AND d.status = #{status}


		${dataScope}
		order by d.parent_id, d.order_num

mapper.update:
update sys_dept
parent_id = #{parentId}, dept_name = #{deptName}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, version = #{version}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where dept_id = #{deptId}
