mapper.select:
select count(1) from sys_user where dr = 0 and user_code = #{userCode}

	and user_id !=#{userId}

limit 1

select user_id, phonenumber from sys_user where dr = 0 and phonenumber = #{phonenumber}

	and user_id !=#{userId}

limit 1

select user_id, email from sys_user where dr = 0 and email = #{email}

	and user_id !=#{userId}

limit 1

mapper.update:
update sys_user
org_id = #{orgId}, salt = #{salt}, psn_id = #{psnId}, user_code = #{userCode}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, version = #{version}, wechatopenid = #{wechatopenid}, user_type = #{userType}, update_time = sysdate()
where user_id = #{userId}
